package Game_GUI;

import java.awt.image.BufferedImage;

public class Shape {
	int x;
	int y;
	BufferedImage img; 
	public Shape() {
		// TODO Auto-generated constructor stub
		
	}
	public int getX() {
		return x;
	}
	public void setX(int x) {
		this.x = x;
	}
	public int getY() {
		return y;
	}
	public void setY(int y) {
		this.y = y;
	}
	public BufferedImage getImg() {
		return img;
	}
	public void setImg(BufferedImage img) {
		this.img = img;
	}

}
